// =============================================================================
// Envelope Builders - Request/Response envelope construction
// =============================================================================
// Provides standardized envelope building for all MCP tools
//
// Input: {
//   action: 'success' | 'error',
//   
//   // For success:
//   data: { ... },
//   correlation_id?: string,
//   audit_event_id?: string,
//   
//   // For error:
//   error_code: string,
//   error_message: string,
//   recoverable?: boolean,
//   suggested_action?: string,
//   
//   // Common:
//   request_id: string,
//   start_time: number,
//   gaap_layer: 'L1' | 'L2' | 'L3' | 'L4' | 'MCP',
//   camdl_anchored?: boolean
// }
//
// Output: Standard GaaP MCP Response Envelope
// =============================================================================

const input = $input.first().json;
const executionMs = input.start_time ? Date.now() - input.start_time : 0;

if (input.action === 'error') {
  // Error response
  return [{
    json: {
      success: false,
      error: {
        code: input.error_code || 'MCP_UNKNOWN_ERROR',
        message: input.error_message || 'An unknown error occurred',
        recoverable: input.recoverable !== undefined ? input.recoverable : false,
        suggested_action: input.suggested_action || null
      },
      meta: {
        request_id: input.request_id || 'unknown',
        execution_ms: executionMs,
        gaap_layer: input.gaap_layer || 'MCP',
        camdl_anchored: false
      }
    }
  }];
}

// Success response
return [{
  json: {
    success: true,
    result: {
      data: input.data || {},
      correlation_id: input.correlation_id || null,
      audit_event_id: input.audit_event_id || null
    },
    meta: {
      request_id: input.request_id || 'unknown',
      execution_ms: executionMs,
      gaap_layer: input.gaap_layer || 'MCP',
      camdl_anchored: input.camdl_anchored || false
    }
  }
}];
