// =============================================================================
// gaap_audit_log_event - Log compliance event with optional CamDL anchoring
// =============================================================================
// GaaP Layer: L4 (Compliance & Audit)
//
// Input params:
//   event_type: string (required) - e.g., 'order.created', 'payment.captured'
//   entity_type: string (required) - e.g., 'order', 'payment', 'customer'
//   entity_id: string (required) - e.g., 'ORD-2025-001'
//   state_change: object (optional) - { previous: {...}, new: {...} }
//   previous_hash: string (optional) - Hash of previous event in chain
//   metadata: object (optional) - Additional context
//   anchor_to_camdl: boolean (optional) - Request immediate CamDL anchoring
//
// Output:
//   audit_event_id: UUID
//   correlation_id: string
//   event_hash: string (SHA-256)
//   camdl_anchored: boolean
//   camdl_anchor_id?: string (if anchored)
// =============================================================================

const crypto = require('crypto');

const input = $input.first().json;
const startTime = input._startTime || Date.now();
const requestId = input.request_id;
const tenantId = input.tenant_context?.tenant_id;
const correlationId = input.tenant_context?.correlation_id || `CRR-${Date.now()}-${crypto.randomBytes(4).toString('hex')}`;

// Validate required params
const params = input.params || {};
const requiredParams = ['event_type', 'entity_type', 'entity_id'];
const missingParams = requiredParams.filter(p => !params[p]);

if (missingParams.length > 0) {
  return [{
    json: {
      _error: true,
      error: {
        code: 'L4_AUDIT_MISSING_PARAMS',
        message: `Missing required parameters: ${missingParams.join(', ')}`,
        recoverable: false
      },
      meta: {
        request_id: requestId,
        execution_ms: Date.now() - startTime,
        gaap_layer: 'L4',
        camdl_anchored: false
      }
    }
  }];
}

// Extract state change info
const previousState = params.state_change?.previous || null;
const newState = params.state_change?.new || null;
const stateChangeSummary = params.state_change 
  ? `${params.event_type}: ${JSON.stringify(Object.keys(params.state_change.new || {}))}`
  : null;

// Compute event hash (SHA-256)
// Canonical format: tenant_id|event_type|entity_type|entity_id|new_state|previous_hash|timestamp
const eventTimestamp = new Date().toISOString();
const canonicalData = [
  tenantId,
  params.event_type,
  params.entity_type,
  params.entity_id,
  JSON.stringify(newState || {}),
  params.previous_hash || '',
  eventTimestamp
].join('|');

const eventHash = crypto.createHash('sha256').update(canonicalData).digest('hex');

// Generate audit event ID
const auditEventId = crypto.randomUUID();

// Prepare for database insert
return [{
  json: {
    _startTime: startTime,
    request_id: requestId,
    tenant_id: tenantId,
    correlation_id: correlationId,
    audit_event_id: auditEventId,
    event_type: params.event_type,
    entity_type: params.entity_type,
    entity_id: params.entity_id,
    previous_state: previousState,
    new_state: newState,
    state_change_summary: stateChangeSummary,
    event_hash: eventHash,
    previous_hash: params.previous_hash || null,
    metadata: params.metadata || {},
    event_timestamp: eventTimestamp,
    anchor_to_camdl: params.anchor_to_camdl || false,
    source_tool: 'gaap_audit_log_event',
    source_workflow: input.meta?.source_workflow || null,
    source_ip: input.source_ip || null
  }
}];

// =============================================================================
// Map AWS CamDL Response
// =============================================================================
const prepareData = $('Prepare Event').first().json;
const insertResult = $('Insert Event').first().json;
const awsResponse = $input.first().json;

return [{
  json: {
    camdl_anchored: true,
    camdl_anchor_id: awsResponse.anchor_id || awsResponse.camdl_anchor_id,
    camdl_block_number: awsResponse.block_number || awsResponse.camdl_block_number,
    camdl_tx_hash: awsResponse.tx_hash || awsResponse.camdl_tx_hash,
    audit_event_id: insertResult.audit_event_id,
    event_hash: insertResult.event_hash,
    correlation_id: prepareData.correlation_id,
    _startTime: prepareData._startTime,
    request_id: prepareData.request_id,
    _aws_endpoint: true
  }
}];

// =============================================================================
// CamDL Anchoring (Local Fallback)
// =============================================================================
// Used when AWS endpoint fails or is disabled.
// Generates a mock CamDL response for development/testing.
// =============================================================================

const prepareData = $('Prepare Event').first().json;
const insertResult = $('Insert Event').first().json;

// Fallback: Generate mock CamDL response
const mockAnchorId = `CAMDL-${Date.now()}-${Math.random().toString(36).substring(2, 8)}`;
const mockBlockNumber = Math.floor(Date.now() / 1000);
const mockTxHash = `0x${require('crypto').randomBytes(32).toString('hex')}`;

return [{
  json: {
    camdl_anchored: true,
    camdl_anchor_id: mockAnchorId,
    camdl_block_number: mockBlockNumber,
    camdl_tx_hash: mockTxHash,
    audit_event_id: insertResult.audit_event_id,
    event_hash: insertResult.event_hash,
    correlation_id: prepareData.correlation_id,
    _startTime: prepareData._startTime,
    request_id: prepareData.request_id,
    _fallback: true
  }
}];

// Build success response (with anchoring)
const anchorData = $('Merge CamDL Result').first().json;

return [{
  json: {
    data: {
      audit_event_id: anchorData.audit_event_id,
      event_hash: anchorData.event_hash,
      camdl_anchor_id: anchorData.camdl_anchor_id,
      camdl_block_number: anchorData.camdl_block_number,
      camdl_tx_hash: anchorData.camdl_tx_hash
    },
    correlation_id: anchorData.correlation_id,
    audit_event_id: anchorData.audit_event_id,
    camdl_anchored: true,
    meta: {
      request_id: anchorData.request_id,
      execution_ms: Date.now() - anchorData._startTime,
      gaap_layer: 'L4',
      camdl_anchored: true,
      aws_endpoint: anchorData._aws_endpoint || false,
      fallback_used: anchorData._fallback || false
    }
  }
}];

// Build success response (without anchoring)
const prepareData = $('Prepare Event').first().json;
const insertResult = $('Insert Event').first().json;

return [{
  json: {
    data: {
      audit_event_id: insertResult.audit_event_id,
      event_hash: insertResult.event_hash
    },
    correlation_id: prepareData.correlation_id,
    audit_event_id: insertResult.audit_event_id,
    camdl_anchored: false,
    meta: {
      request_id: prepareData.request_id,
      execution_ms: Date.now() - prepareData._startTime,
      gaap_layer: 'L4',
      camdl_anchored: false
    }
  }
}];
